    function hexToAdjustedRgba(hex, opacity = 1, adjustFactor = 0) {
        hex = hex.replace('#', '');
        const r = parseInt(hex.slice(0, 2), 16);
        const g = parseInt(hex.slice(2, 4), 16);
        const b = parseInt(hex.slice(4, 6), 16);

        adjustFactor = Math.max(-1, Math.min(1, adjustFactor));

        const adjust = (c) => (adjustFactor < 0 ? Math.floor(c * (1 + adjustFactor)) : Math.floor(c + (255 - c) * adjustFactor));

        const rr = adjust(r);
        const gg = adjust(g);
        const bb = adjust(b);

        if (opacity === 1) {
            return `#${rr.toString(16).padStart(2, '0')}${gg.toString(16).padStart(2, '0')}${bb.toString(16).padStart(2, '0')}`;
        }

        return `rgba(${rr}, ${gg}, ${bb}, ${opacity})`;
    }

